(function($){$.extend({stayInWebApp:function(options){if(typeof options=="string"){options={selector:options}}console.log(options);if(("standalone"in window.navigator)&&window.navigator.standalone){if(!options.selector){options.selector='a'}$(options.selector).click(function(event){if($(this).attr("target")==undefined||$(this).attr("target")==""||$(this).attr("target")=="_self"){var dest=$(this).attr("href");var stay=false;if(!dest.match(/^http(s?)/g)){stay=true}else if(options.absToStay){for(var i=0;i<options.absToStay.length;i++){if(dest.indexOf(options.absToStay[i])>0){stay=true;break}}}if(stay){event.preventDefault();self.location=dest}}})}}})})(jQuery);